package main

import (
	"fmt"
)

type window struct {
	s []rune
	c map[rune]int
}

func minWindow(s string, t string) string {
	m := map[rune]int{}
	for _, r := range t {
		m[r] += 1
	}
	ws := []window{}
	minS := []rune{}
	for _, c := range s {
		ok := false
		if _, ok = m[c]; ok {
			ws = append(ws, window{c: map[rune]int{}})
		}
		for i := range ws {
			if len(minS) > 0 && len(ws[i].s) >= len(minS) { // this window can't grow
				continue
			}
			if ok && m[c] > ws[i].c[c] {
				ws[i].c[c] += 1
			}
			ws[i].s = append(ws[i].s, c)

			if len(ws[i].c) == len(m) && (len(minS) == 0 || len(ws[i].s) < len(minS)) {
				wok := true
				for wc, wcl := range ws[i].c { // check actual match
					if m[wc] != wcl {
						wok = false
					}
				}
				if wok {
					minS = ws[i].s
				}
			}
		}
	}

	return string(minS)
}

func main() {
	// fmt.Println(minWindow("ADOBECODEBANC", "ABC"))
	// fmt.Println(minWindow("ADOBECODEBANC", "ECO"))
	// fmt.Println(minWindow("ADOBECODEBANC", "CDE"))
	// fmt.Println(minWindow("a", "aa"))
	fmt.Println(minWindow("", ""))
}
