package main

import (
	"fmt"
	"reflect"
)

type triplet struct{ a, b, c int }

func sort3(a, b, c int) triplet {
	switch {
	case a <= b && b <= c:
		return triplet{a, b, c}
	case a <= c && c <= b:
		return triplet{a, c, b}
	case b <= a && a <= c:
		return triplet{b, a, c}
	case b <= c && c <= a:
		return triplet{b, c, a}
	case c <= a && a <= b:
		return triplet{c, a, b}
	default:
		return triplet{c, b, a}
	}
}

// Time: O(n^2)
// Space: O(??)
func threeSum(nums []int) [][]int {
	// For every number, store its frequency.
	// Also make a compact version of _nums_, with up to
	// 2 of each unique number (up to 3 for the number 0).
	numFreq := map[int]int{}
	rNums := []int{}
	for _, num := range nums {
		numFreq[num]++
		if numFreq[num] < 2 || (num == 0 && numFreq[num] < 3) {
			rNums = append(rNums, num)
		}
	}

	// Because triplets must be unique, we use a map to a triplet struct,
	// and we sort the triplet ascending before adding it to the map.
	tripletMap := map[triplet]struct{}{}
	// Go through every pair...
	for i := range rNums {
		for j := i + 1; j < len(rNums); j++ {
			// See if there's a number to complete the triplet...
			complement := -(rNums[i] + rNums[j])
			freq := numFreq[complement]

			// _complement_ needs to exist on _rNums_, but also not
			// be already used on rNums[i] or rNums[j].
			requiredFreq := 1
			if complement == rNums[i] {
				requiredFreq++
			}
			if complement == rNums[j] {
				requiredFreq++
			}

			// If there are enough instances of _complement_, add it...
			if freq >= requiredFreq {
				// But sort the triplet first, to avoid duplicates.
				tripletMap[sort3(rNums[i], rNums[j], complement)] = struct{}{}
			}
		}
	}

	// Re-format the triplets into [][]int type.
	tuples := [][]int{}
	for t := range tripletMap {
		tuples = append(tuples, []int{t.a, t.b, t.c})
	}
	return tuples
}

